2s 2s backwards;position:relative;overflow:hidden}.btn-carpinteria-2:before{content:'';position:absolute;top:-50%;left:-50%;width:200%;height:200%;background:linear-gradient(transparent,rgba(255,255,255,0.3),transparent);transform:rotate(30deg);transition:1.2s;opacity:0}.btn-carpinteria-2:hover:before{opacity:1;left:100%}.btn-carpinteria-2:hover{background:linear-gradient(90deg,var(--wood-primary) 60%,var(--wood-accent) 100%);color:var(--wood-light);transform:translateY(-5px) scale(1.05);box-shadow:0 12px 30px rgba(78,138,74,0.22)}@keyframes fadeIn{0%{opacity:0}100%{opacity:1}}@keyframes slideUp{0%{opacity:0;transform:translateY(40px)}80%{opacity:0.8}100%{opacity:1;transform:translateY(0)}}@keyframes bounceIn{0%{opacity:0;transform:scale(0.6) translateY(-120px)}60%{opacity:0.8;transform:scale(1.08) translateY(20px)}80%{transform:scale(0.96)}100%{opacity:1;transform:scale(1) translateY(0)}}@keyframes pulse{0%,100%{transform:scale(1)}50%{transform:scale(1.05);box-shadow:0 10px 30px rgba(212,175,55,0.4),0 0 0 4px rgba(212,175,55,0.2)}}@keyframes float{0%{transform:translateY(0) rotate(0deg)}50%{transform:translateY(15px) rotate(5deg)}100%{transform:translateY(-15px) rotate(-5deg)}}@keyframes bounce{0%,100%{transform:translateY(0)}50%{transform:translateY(-10px)}}@keyframes starShine{0%,100%{opacity:1;filter:drop-shadow(0 2px 6px rgba(246,194,62,0.4))}50%{opacity:0.7;filter:drop-shadow(0 0 0 transparent)}}@keyframes glowText{0%,100%{text-shadow:0 2px 10px rgba(89,63,41,0.5),0 0 30px rgba(212,175,55,0.4)}50%{text-shadow:0 2px 15px rgba(89,63,41,0.6),0 0 40px rgba(212,175,55,0.6)}}@media (max-width:991px){.logo{width:160px;height:160px}h1{font-size:2.8rem}.brand{font-size:3rem}.lead{font-size:1.4rem}.icons-jumbo{font-size:2.2rem}.hero-content{padding:40px 0 50px 0}.beneficios-section{padding:70px 0 60px 0}.card-beneficio{padding:35px 15px 30px 15px;min-height:260px}.beneficios-title{font-size:2.4rem}}@media (max-width:767px){.logo{width:140px;height:140px}h1{font-size:2.4