  <script>
    const capa = document.getElementById('interaccion');
    const audio = document.getElementById('musica');
      audio.currentTime = 14; // Cambia el número para ajustar el inicio
    capa.addEventListener('click', () => {
      audio.play().catch(err => console.warn("Error al reproducir:", err));
      capa.remove(); // Elimina la capa invisible después del primer clic
    });
  </script>
