<!-- Scripts -->
<script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/js/bootstrap.bundle.min.js"></script>
<script>
// Efectos visuales adicionales
document.addEventListener('DOMContentLoaded', function() {
    // Crear partículas de polvo de madera
    const heroSection = document.querySelector('.hero-wood');
    for (let i = 0; i < 20; i++) {
        let particle = document.createElement('div');
        particle.classList.add('particle');
        
        // Posición aleatoria
        particle.style.left = Math.random() * 100 + '%';
        particle.style.top = Math.random() * 100 + '%';
        
        // Tamaño aleatorio
        const size = Math.random() * 6 + 3;
        particle.style.width = size + 'px';
        particle.style.height = size + 'px';
        
        // Animación personalizada
        particle.style.animation = `float ${Math.random() * 10 + 10}s infinite alternate`;
        particle.style.animationDelay = Math.random() * 5 + 's';
        
        heroSection.appendChild(particle);
    }
    
    // Efecto de elevación para los botones
    const buttons = document.querySelectorAll('.btn-carpinteria, .btn-carpinteria-2');
    buttons.forEach(btn => {
        btn.addEventListener('mouseover', function() {
            this.style.transform = 'translateY(-8px) scale(1.05)';
        });
        btn.addEventListener('mouseout', function() {
            this.style.transform = '';
        });
    });
});

// Protección de contenido (minimizada pero funcional)
document.addEventListener('contextmenu',e=>e.preventDefault());document.addEventListener('selectstart',e=>e.preventDefault());document.addEventListener('copy',e=>e.preventDefault());document.addEventListener('cut',e=>e.preventDefault());document.addEventListener('paste',e=>e.preventDefault());document.addEventListener('keydown',function(e){if(e.keyCode===123)e.preventDefault();if(e.ctrlKey&&e.shiftKey&&['I','J','C','K','L','i','j','c','k','l'].includes(e.key))e.preventDefault();if(e.ctrlKey&&(e.key==='u'||e.key==='U'))e.preventDefault();if(e.ctrlKey&&(e.key==='s'||e.key==='S'))e.preventDefault();if(e.ctrlKey&&(e.key==='p'||e.key==='P'))e.preventDefault();if(e.ctrlKey&&['+','-','=','_'].includes(e.key))e.preventDefault()});window.addEventListener('wheel',function(e){if(e.ctrlKey)e.preventDefault()},{passive:false});document.addEventListener('dragstart',e=>e.preventDefault());
</script>
